package decoding;

public class ByteValue {
	byte value;
	
//This object memorizes a byte value and allows to modify it through a setter
	public ByteValue(byte value) {
		// TODO Auto-generated constructor stub
		this.value = value;
	}

	public byte getValue() {
		return value;
	}
	public void setValue(byte value) {
		this.value = value;
	}

}
